import React from 'react';

class DoublesContainer extends React.Component{
	state = {
		algorithm1: "quicksort",
		algorithm2: "heapsort"
	}

	algorithm1Change = (event) => {
		this.setState({
			algorithm1: event.target.value
		})
	}

	algorithm2Change = (event) => {
		this.setState({
			algorithm2: event.target.value
		})
	}

	render(){
		return (
			<div className="doubleContainer">
				{this.props.array.map(elem => (
					<BarElem value={elem.value * 0.35} key={elem.key}/>
				))}
				{this.props.array.map(elem => (
					<BarElem value={elem.value * 0.35} key={elem.key}/>
				))}
			</div>
		);
	}

}

export default DoubleContainer;
