import React from 'react';
import './App.css';
import SingleContainer from './components/SingleContainer';
import DoubleContainer from './components/DoubleContainer';
import MultipleContainer from './components/MultipleContainer';
import Header from './components/Header';
//import bubblesort from './algorithms/bubblesort';

import {
  HashRouter as Router,
  Switch,
  Route,
} from "react-router-dom";

class App extends React.Component {
  state = {
    array: [], 
    active: 0
  }

  handleNewArray = () => {
    const arr = [];
    for (var i = 0; i<100; i++){
      var rand = Math.ceil(Math.random() * 100);
      arr.push({key: i, value: rand});
    }
    this.setState({
      array: arr
    })
  }

  componentDidMount(){
    this.handleNewArray();
    //const sorted = bubblesort(this.state.array);
  }

  timer = (ms) => {
    return new Promise(res => setTimeout(res, ms));
  }

  bubblesort = async (list, delay) => {
    const length = list.length;
    for(var i = length - 1; i>=0; i--){
      for(var j = 1; j<i; j++){
        if(list[j-1].value > list[j].value){
          let temp = list[j-1];
          list[j-1] = list[j];
          list[j] = temp;
        }
        this.setState({
          array: list
        })
        await this.timer(5);
      }

    }
    return list
  }


  onStart = () => {
    console.log(this.state.array);
    //const sorted = this.bubblesort(this.state.array, 1).then(value => console.log(value));
    this.bubblesort(this.state.array);
  }

  render(){
    return (
      <Router>
        <div className="App">
          <Header handleNewArray={this.handleNewArray} onStart={this.onStart}/>
          <Switch>
            <Route path='/doubleContainer'> 
              <DoubleContainer array={this.state.array} arraySize={this.state.array.length}/>
            </Route>
            <Route path='/multipleContainer'> 
              <MultipleContainer array={this.state.array} arraySize={this.state.array.length} />
            </Route>
            <Route path='/'> 
              <SingleContainer array={this.state.array} arraySize={this.state.array.length} />
            </Route>
          </Switch>
        </div>
    </Router>
  );
}

}

export default App;
