import React from 'react';
import './App.css';
import SingleContainer from './components/SingleContainer';
import DoubleContainer from './components/DoubleContainer';
import MultipleContainer from './components/MultipleContainer';
import Header from './components/Header';
//import bubblesort from './algorithms/bubblesort';

import {
  HashRouter as Router,
  Switch,
  Route,
} from "react-router-dom";

class App extends React.Component {
  state = {
    array: [], 
    active1: 100,
    active2: 100
  }

  handleNewArray = () => {
    const arr = [];
    for (var i = 0; i<100; i++){
      var rand = Math.ceil(Math.random() * 100);
      arr.push({key: i, value: rand});
    }
    this.setState({
      array: arr
    })
  }

  componentDidMount(){
    this.handleNewArray();
  }

  timer = (ms) => {
    return new Promise(res => setTimeout(res, ms));
  }

  //bubble sort

  bubblesort = async (list, delay) => {
    const length = list.length;
    for(var i = length - 1; i>=0; i--){
      for(var j = 1; j<=i; j++){

        if(list[j-1].value > list[j].value){
          let temp = list[j-1];
          list[j-1] = list[j];
          list[j] = temp;
        }
        this.setState({
          array: list,
          active1: list[j-1].key,
          active2: list[j].key
        })
        await this.timer(0);
      }
      this.setState({
        active1: 100,
        active2: 100
      })
    }
    return list
  }

  //merge sort

  merge =  (left, right) => {
    let list = [];
    while(left.length && right.length){
      if(left[0].value < right[0].value){
        list.push(left.shift());
      }else{
        list.push(right.shift());
      }
      var res = [...list, ...left, ...right];
      this.setState({array: res})
      //await this.timer(5);
    }
    return res
  }

  mergesort = (list) => {
    let half = list.length/2;
    if(list.length < 2){
      return list;
    }

    const left = list.splice(0,half);

    let res = this.merge(this.mergesort(left), this.mergesort(list));
    return res
  }

  selection = async (list) => {
    let length = list.length;

    for(var i = 0; i < length; i++) {

        var min = i;
        for(var j = i + 1; j < length; j++) {
            // Executing statement comparison
            if(list[min].value > list[j].value) {
                // Updating our current min index to iterate
                min = j;
            }
            this.setState({
              array: list,
              active1: list[j].key,
              active2: list[min].key
            })
            await this.timer(2)
        }

        // Swaping values
        if(min !== i) {
            let temp = list[i];
            list[i] = list[min];
            list[min] = temp;
        }
    }
    this.setState({
      active1: 100,
      active2: 100
    })

    return list;
  }

  onStart = () => {
    this.selection(this.state.array);
  }

  render(){
    return (
      <Router>
        <div className="App">
          <Header handleNewArray={this.handleNewArray} onStart={this.onStart}/>
          <Switch>
            <Route path='/doubleContainer'> 
              <DoubleContainer array={this.state.array} arraySize={this.state.array.length}/>
            </Route>
            <Route path='/multipleContainer'> 
              <MultipleContainer array={this.state.array} arraySize={this.state.array.length} />
            </Route>
            <Route path='/'> 
              <SingleContainer array={this.state.array} 
                arraySize={this.state.array.length} 
                active1={this.state.active1}
                active2={this.state.active2}
              />
            </Route>
          </Switch>
        </div>
    </Router>
  );
}

}

export default App;
