import React from 'react';
import './App.css';
import SingleContainer from './components/SingleContainer';
import Header from './components/Header';

class App extends React.Component {
  state = {
    array: []
  }

  handleNewArray = () => {
    const arr = new Array(100);
    for (var i = 0; i<100; i++){
      var rand = Math.floor(Math.random() * 600);
      arr.push(rand);
    }
    this.setState({
      array: arr
    })
  }

  render(){
    return (
    <div className="App">
      <Header handleNewArray={this.handleNewArray}/>
      <SingleContainer array={this.state.array}/>
    </div>
  );
  }

}

export default App;
