import React from 'react';
import BarElem from './BarElem';
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';

class DoubleContainer extends React.Component{
	state = {
		algorithm1: "quicksort",
		algorithm2: "heapsort",
		array1: [],
		array2: []
	}

	algorithm1Change = (event) => {
		this.setState({
			algorithm1: event.target.value
		})
	}

	algorithm2Change = (event) => {
		this.setState({
			algorithm2: event.target.value
		})
	}

	componentDidMount(){
		this.setState({
			array1: this.props.array,
			array2: this.props.array
		});
	}

	render(){
		return (
			<div className="doubleContainer cont">
				<div>

				{this.state.array1.map(elem => (
					<BarElem value={elem.value * 0.35} key={elem.key} width={0.5}/>
				))}
				<div style={{textAlign: 'center'}}>
				<FormControl variant="filled" style={{minWidth: "16vw"}}>
				        <InputLabel id="demo-simple-select-label" >Select An Algorithm</InputLabel>
				        <Select
				          labelId="demo-simple-select-filled-label"
         				  id="demo-simple-select-filled"
				          value={this.state.algorithm}
				          onChange={(ev) => this.algorithm1Change(ev)}
				        >
				          <MenuItem value="quicksort">quicksort</MenuItem>
				          <MenuItem value="heapsort">Heap Sort</MenuItem>
				          <MenuItem value="bubblesort">Bubble Sort</MenuItem>
				          <MenuItem value="insertionsort">Insertion Sort</MenuItem>
				          <MenuItem value="selectionsort">Selection Sort</MenuItem>
				          <MenuItem value="treesort">Tree Sort</MenuItem>
				          <MenuItem value="simplesort">Simple Sort</MenuItem>
				        </Select>
			      </FormControl>
			      </div>
				</div>
				<br/>
				<div>

				{this.state.array2.map(elem => (
					<BarElem value={elem.value * 0.35} key={elem.key} width={0.5}/>
				))}
				<div style={{textAlign: 'center'}}>
				<FormControl variant="filled" style={{minWidth: "15vw"}}>
				        <InputLabel id="demo-simple-select-label" >Select An Algorithm</InputLabel>
				        <Select
				          labelId="demo-simple-select-filled-label"
         				  id="demo-simple-select-filled"
				          value={this.state.algorithm}
				          onChange={(ev) => this.algorithm2Change(ev)}
				        >
				          <MenuItem value="quicksort">quicksort</MenuItem>
				          <MenuItem value="heapsort">Heap Sort</MenuItem>
				          <MenuItem value="bubblesort">Bubble Sort</MenuItem>
				          <MenuItem value="insertionsort">Insertion Sort</MenuItem>
				          <MenuItem value="selectionsort">Selection Sort</MenuItem>
				          <MenuItem value="treesort">Tree Sort</MenuItem>
				          <MenuItem value="simplesort">Simple Sort</MenuItem>
				        </Select>
			      </FormControl>
			      </div>
				</div>
			</div>
		);
	}

}

export default DoubleContainer;
