import React from 'react';
import BarElem from './BarElem';
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';

class SingleContainer extends React.Component{
	state = {
		array: [],
		algorithm: "quicksort"
	}

	changeArr = () => {}

	handleNewArray = () => {
		const arr = new Array(100);
		for (var i = 0; i<100; i++){
			var rand = Math.floor(Math.random() * 600);
			arr.push(rand);
		}
		this.setState({
			array: arr
		})
	}

	handleChange = (event) => {
    	this.setState({
    		algorithm: event.target.value
    	})
  	}

	render(){
		return(
			<div className="singleContainer" onClick={() => this.changeArr()}>

				{this.props.array.map(elem => (
					<BarElem value={elem} key={elem}/>
				))}
				<div style={{height: "8vh", backgroundColor: "#5f5f5f"}}>
					<FormControl>
				        <InputLabel id="demo-simple-select-label">Age</InputLabel>
				        <Select
				          labelId="demo-simple-select-label"
				          id="demo-simple-select"
				          value={this.state.algorithm}
				          onChange={(ev) => this.handleChange(ev)}
				        >
				          <MenuItem value="quicksort">quicksort</MenuItem>
				          <MenuItem value="heapsort">Heap Sort</MenuItem>
				          <MenuItem value="bubblesort">Bubble Sort</MenuItem>
				        </Select>
			      </FormControl>
				</div>
			</div>
		);
	}
}

export default SingleContainer;
