import React from 'react';
import './App.css';
import SingleContainer from './components/SingleContainer';
import DoubleContainer from './components/DoubleContainer';
import MultipleContainer from './components/MultipleContainer';
import Header from './components/Header';

import {
  HashRouter as Router,
  Switch,
  Route,
} from "react-router-dom";

class App extends React.Component {
  state = {
    array: []
  }

  handleNewArray = () => {
    const arr = new Array(100);
    for (var i = 0; i<100; i++){
      var rand = Math.ceil(Math.random() * 100);
      arr.push({key: i, value: rand});
    }
    this.setState({
      array: arr
    })
  }

  componentDidMount(){
    this.handleNewArray();
  }

  render(){
    return (
      <Router>
        <div className="App">
          <Header handleNewArray={this.handleNewArray} />
          <Switch>
            <Route path='/doubleContainer'> 
              <DoubleContainer />
            </Route>
            <Route path='/multipleContainer'> 
              <MultipleContainer />
            </Route>
            <Route path='/'> 
              <SingleContainer array={this.state.array}/>
            </Route>
          </Switch>
        </div>
    </Router>
  );
  }

}

export default App;
