import React from 'react';
import BarElem from './BarElem';
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';

class MultipleContainer extends React.Component{
	state = {
		alg1: "",
		alg2: "",
		alg3: "",
		alg4: ""
	}

	algorithm1Change = (ev) => {
		this.setState({
			alg1: ev.target.value
		})
	}

	algorithm2Change = (ev) => {
		this.setState({
			alg1: ev.target.value
		})
	}

	algorithm3Change = (ev) => {
		this.setState({
			alg1: ev.target.value
		})
	}

	algorithm4Change = (ev) => {
		this.setState({
			alg1: ev.target.value
		})
	}

	render(){
		return (
		<div className="multipleContainer cont">
			<div className="square">
				{this.props.array.map(elem => (
					<BarElem value={elem.value * 0.35} key={elem.key} width={0.22}/>
				))}
				<div style={{textAlign: 'center'}}>
				<FormControl variant="filled" style={{minWidth: "16vw"}}>
				        <InputLabel id="demo-simple-select-label" >Select An Algorithm</InputLabel>
				        <Select
				          labelId="demo-simple-select-filled-label"
         				  id="demo-simple-select-filled"
				          value={this.state.algorithm}
				          onChange={(ev) => this.algorithm1Change(ev)}
				        >
				          <MenuItem value="quicksort">quicksort</MenuItem>
				          <MenuItem value="heapsort">Heap Sort</MenuItem>
				          <MenuItem value="bubblesort">Bubble Sort</MenuItem>
				          <MenuItem value="insertionsort">Insertion Sort</MenuItem>
				          <MenuItem value="selectionsort">Selection Sort</MenuItem>
				          <MenuItem value="treesort">Tree Sort</MenuItem>
				          <MenuItem value="simplesort">Simple Sort</MenuItem>
				        </Select>
			      </FormControl>
			      </div>
			</div>
			<div className="square">
			{this.props.array.map(elem => (
				<BarElem value={elem.value * 0.35} key={elem.key} width={0.22}/>
			))}
			<div style={{textAlign: 'center'}}>
				<FormControl variant="filled" style={{minWidth: "16vw"}}>
				        <InputLabel id="demo-simple-select-label" >Select An Algorithm</InputLabel>
				        <Select
				          labelId="demo-simple-select-filled-label"
         				  id="demo-simple-select-filled"
				          value={this.state.algorithm}
				          onChange={(ev) => this.algorithm2Change(ev)}
				        >
				          <MenuItem value="quicksort">quicksort</MenuItem>
				          <MenuItem value="heapsort">Heap Sort</MenuItem>
				          <MenuItem value="bubblesort">Bubble Sort</MenuItem>
				          <MenuItem value="insertionsort">Insertion Sort</MenuItem>
				          <MenuItem value="selectionsort">Selection Sort</MenuItem>
				          <MenuItem value="treesort">Tree Sort</MenuItem>
				          <MenuItem value="simplesort">Simple Sort</MenuItem>
				        </Select>
			      </FormControl>
			      </div>
			</div>
			<div className="square">
			{this.props.array.map(elem => (
				<BarElem value={elem.value * 0.35} key={elem.key} width={0.22}/>
			))}
			<div style={{textAlign: 'center'}}>
				<FormControl variant="filled" style={{minWidth: "16vw"}}>
				        <InputLabel id="demo-simple-select-label" >Select An Algorithm</InputLabel>
				        <Select
				          labelId="demo-simple-select-filled-label"
         				  id="demo-simple-select-filled"
				          value={this.state.algorithm}
				          onChange={(ev) => this.algorithm3Change(ev)}
				        >
				          <MenuItem value="quicksort">quicksort</MenuItem>
				          <MenuItem value="heapsort">Heap Sort</MenuItem>
				          <MenuItem value="bubblesort">Bubble Sort</MenuItem>
				          <MenuItem value="insertionsort">Insertion Sort</MenuItem>
				          <MenuItem value="selectionsort">Selection Sort</MenuItem>
				          <MenuItem value="treesort">Tree Sort</MenuItem>
				          <MenuItem value="simplesort">Simple Sort</MenuItem>
				        </Select>
			      </FormControl>
			      </div>
			</div>
			<div className="square">
				{this.props.array.map(elem => (
					<BarElem value={elem.value * 0.35} key={elem.key} width={0.22}/>
				))}
				<div style={{textAlign: 'center'}}>
					<FormControl variant="filled" className="algorithmSelect">
					        <InputLabel id="demo-simple-select-label" >Select An Algorithm</InputLabel>
					        <Select
					          labelId="demo-simple-select-filled-label"
	         				  id="demo-simple-select-filled"
					          value={this.state.algorithm}
					          onChange={(ev) => this.algorithm4Change(ev)}
					        >
					          <MenuItem value="quicksort">quicksort</MenuItem>
					          <MenuItem value="heapsort">Heap Sort</MenuItem>
					          <MenuItem value="bubblesort">Bubble Sort</MenuItem>
					          <MenuItem value="insertionsort">Insertion Sort</MenuItem>
					          <MenuItem value="selectionsort">Selection Sort</MenuItem>
					          <MenuItem value="treesort">Tree Sort</MenuItem>
					          <MenuItem value="simplesort">Simple Sort</MenuItem>
					        </Select>
				    </FormControl>
				</div>
			</div>
		</div>
		);
	}
}

export default MultipleContainer;
