import React from 'react';
import './App.css';
import SingleContainer from './components/SingleContainer';
import DoubleContainer from './components/DoubleContainer';
import MultipleContainer from './components/MultipleContainer';
import Header from './components/Header';
//import bubblesort from './algorithms/bubblesort';

import {
  HashRouter as Router,
  Switch,
  Route,
} from "react-router-dom";

const requestAnimationFrame = window.requestAnimationFrame

class App extends React.Component {
  state = {
    array: [], 
    active1: 101,
    active2: 101,
    algorithm: 'bubblesort',
    active: true
  }

  testAnimation = () => {
    if (this.state.active){
      this.setState({
        algorithm: 'insertionsort',
        active: !this.state.active
      })
    }else{
      this.setState({
        algorithm: 'selectionsort',
        active: !this.state.active
      })
    }
    //requestAnimationFrame(this.testAnimation)
  }

  handleNewArray = () => {
    const arr = [];
    for (var i = 0; i<100; i++){
      var rand = Math.ceil(Math.random() * 100);
      arr.push({key: i, value: rand});
    }
    this.setState({
      array: arr
    })
  }

  componentDidMount(){
    this.handleNewArray();
  }

  algorithmSelection = (event) => {
      this.setState({
        algorithm: event.target.value
      })
    }

  timer = (ms) => {
    return new Promise(res => setTimeout(res, ms));
  }

  //bubble sort
  bubblesort = async (list, delay) => {
    const length = list.length;
    for(var i = length - 1; i>=0; i--){
      for(var j = 1; j<=i; j++){

        if(list[j-1].value > list[j].value){
          let temp = list[j-1];
          list[j-1] = list[j];
          list[j] = temp;
        }
        this.setState({
          array: list,
          active1: list[j-1].key,
          active2: list[j].key
        })
        await this.timer(15);
      }
      this.setState({
        active1: 101,
        active2: 101
      })
    }
    return list
  }



  selectionsort = async (list) => {
    let length = list.length;

    for(var i = 0; i < length; i++) {

        var min = i;
        for(var j = i + 1; j < length; j++) {
            // Executing statement comparison
            if(list[min].value > list[j].value) {
                // Updating our current min index to iterate
                min = j;
            }
            this.setState({
              array: list,
              active1: list[j].key,
              active2: list[min].key
            })
            await this.timer(15)
        }

        // Swaping values
        if(min !== i) {
            let temp = list[i];
            list[i] = list[min];
            list[min] = temp;
        }
    }
    this.setState({
      active1: 100,
      active2: 100
    })

    return list;
  }

  insertionsort = async (list) => {
    let auxArray = list.slice();
    let length = auxArray.length;

    for (let i = 1; i < length; i++) {
        let key = list[i];
        let j = i - 1;
        while (j >= 0 && auxArray[j].value > key.value) {
          this.setState({
            array: auxArray,
            active1: auxArray[j].key,
            active2: auxArray[i].key
          })
          await this.timer(15);
          auxArray[j + 1] = auxArray[j];
          auxArray[j] = {key: 100, value: auxArray[j].value};
          j--;
        }
        auxArray[j + 1] = key;
    }

    //get rid of active "red" bars
    this.setState({
      array: auxArray,
      active1: 101,
      active2: 101
    })
  }

  //------------------------------ to implement --------------------------//


  delayLoop = (fn, delay) => {
    return (name) => {
      setTimeout(() => {
        fn(name);
      }, delay * 1000);
    }
  }


  merge = (left, right) => {
    var list = [];
    while(left.length && right.length){
      if(left[0].value <= right[0].value){
        list.push(left[0]);
        left = left.slice(1);
      }else{
        list.push(right[0]);
        right = right.slice(1);
      }
    }

    while (left.length){
      list.push(left.shift());
    }

    while (right.length){
      list.push(right.shift());
    }

    return list
  }

  mergesort = (list) => {
    if(list.length < 2){
      return list;
    }else{
      var half = Math.floor(list.length / 2);
      var left = list.slice(0, half);
      var right = list.slice(half, list.length);
      var res = this.merge(this.mergesort(left), this.mergesort(right));
      this.setState({array: res})

      return res;
    }

  }



  swap = (items, leftIndex, rightIndex) => {
    var temp = items[leftIndex];
    items[leftIndex] = items[rightIndex];
    items[rightIndex] = temp;
  }

  heapify = (input, i, length) => {
    var left = 2 * i + 1;
    var right = 2 * i + 2;
    var max = i;
    //var length = input.length;

    if (left < length && input[left].value > input[max].value) {
      max = left;
    }
    if (right < length && input[right].value > input[max].value)     {
      max = right;
    }
    if (max !== i) {
      this.swap(input, i, max);
      this.heapify(input, max, length);
    }
  }

  heapsort = (input) => {
    var length = input.length;
    for (var i = Math.floor(length / 2); i >= 0; i--){
      this.heapify(input, i, length);
    }

    for (i = input.length - 1; i > 0; i--) {
      this.swap(input, 0, i);
      length--;
      this.heapify(input, 0, length);
    }

    this.setState({array: input})
  }


  //quicksort

  partition = (items, left, right) => {
    var pivot = items[Math.floor((right + left) / 2)]; //middle element
    var i = left; //left pointer
    var j = right; //right pointer
    while (i <= j){
      while (items[i].value < pivot.value){
        i++;
      }
      while (items[j].value > pivot.value){
        j--;
      }
      if (i <= j) {
        this.swap(items, i, j); //swap two elements
        i++;
        j--;
      }
    }
    return i;
  }

  quicksort = (items, left, right) => {
    var index;
    if (items.length > 1) {
      index = this.partition(items, left, right); //index returned from partition
      if (left < index - 1) { //more elements on the left side of the pivot
        this.quicksort(items, left, index - 1);
      }
      if (index < right) { //more elements on the right side of the pivot
        this.quicksort(items, index, right);
      }
    }
    this.setState({array: items});
    return items;
  }



  onStart = () => {
    let algorithm = this.state.algorithm;
    if(algorithm === 'bubblesort'){
      this.bubblesort(this.state.array)
    }else if (algorithm === 'selectionsort') {
      this.selectionsort(this.state.array);
    }else if(algorithm === 'insertionsort'){
      this.insertionsort(this.state.array)
    }else if(algorithm === 'mergesort'){
      this.mergesort(this.state.array);
    }else if(algorithm === 'heapsort'){
      this.heapsort(this.state.array);
    }else if(algorithm === 'quicksort'){
      this.quicksort(this.state.array, 0, this.state.array.length - 1);
    }
    //requestAnimationFrame(this.testAnimation);
  };






  render(){
    return (
      <Router>
        <div className="App">
          <Header handleNewArray={this.handleNewArray} onStart={this.onStart}/>
          <Switch>
            <Route path='/doubleContainer'> 
              <DoubleContainer array={this.state.array} />
            </Route>
            <Route path='/multipleContainer'> 
              <MultipleContainer array={this.state.array} />
            </Route>
            <Route path='/'> 
              <SingleContainer array={this.state.array} 
                arraySize={this.state.array.length} 
                active1={this.state.active1}
                active2={this.state.active2}
                algorithm={this.state.algorithm}
                algorithmSelection={this.algorithmSelection}
              />
            </Route>
          </Switch>
        </div>
    </Router>
  );
}

}

export default App;
