import React from 'react';
import BarElem from './BarElem';

class SingleContainer extends React.Component{
	state = {
		array: []
	}

	changeArr = () => {
		const temp1 = this.state.array[0]
		const temp2 = this.state.array[12]
		console.log(temp1);
		console.log(temp2);
	}

	handleNewArray = () => {
		const arr = new Array(100);
		for (var i = 0; i<arr.length; i++){
			var rand = Math.floor(Math.random() * 600);
			arr.push(rand);
		}
		this.setState({
			array: arr
		})
	}

	render(){
		return(
			<div className="singleContainer" onClick={() => this.changeArr()}>

				{this.state.array.map(elem => (
					<BarElem value={elem} key={elem}/>
				))}
				<div style={{height: "8vh", backgroundColor: "#ff0000", width: "7vw"}} 
					onClick={this.handleNewArray}
				>
				</div>
			</div>
		);
	}
}

export default SingleContainer;
