import React from 'react';
import './App.css';
import SingleContainer from './components/SingleContainer';
import DoubleContainer from './components/DoubleContainer';
import MultipleContainer from './components/MultipleContainer';
import Header from './components/Header';
//import bubblesort from './algorithms/bubblesort';

import {
  HashRouter as Router,
  Switch,
  Route,
} from "react-router-dom";

//const requestAnimationFrame = window.requestAnimationFrame

class App extends React.Component {
  state = {
    array: [], 
    array2: [],
    array3: [],
    array4: [],
    auxArray: [],
    active1: 101,
    active2: 101,
    algorithm: 'bubblesort',
    algorithm2: 'insertionsort',
    algorithm3: 'selectionsort',
    algorithm4: 'heapsort'
  }

  handleNewArray = () => {
    const arr = [];
    for (var i = 0; i<100; i++){
      var rand = Math.ceil(Math.random() * 100);
      arr.push({key: i, value: rand});
    }
    this.setState({
      array: arr, 
      array2: arr,
      array3: arr,
      array4: arr
    })
  }

  componentDidMount(){
    this.handleNewArray();
  }

  algorithmSelection = (event) => {
    console.log(event.target);
    this.setState({
      algorithm: event.target.value
    })
  }

  algorithmSelection2 = (event) => {
    this.setState({
      algorithm2: event.target.value
    })
  }

  algorithmSelection3 = (event) => {
    this.setState({
      algorithm3: event.target.value
    })
  }

  algorithmSelection4 = (event) => {
    this.setState({
      algorithm4: event.target.value
    })
  }


  timer = (ms) => {
    return new Promise(res => setTimeout(res, ms));
  }

  //bubble sort
  bubblesort = async (list, delay) => {
    const length = list.length;
    for(var i = length - 1; i>=0; i--){
      for(var j = 1; j<=i; j++){

        if(list[j-1].value > list[j].value){
          let temp = list[j-1];
          list[j-1] = list[j];
          list[j] = temp;
        }
        this.setState({
          array: list,
          active1: list[j-1].key,
          active2: list[j].key
        })
        await this.timer(15);
      }
      this.setState({
        active1: 101,
        active2: 101
      })
    }
    return list
  }



  selectionsort = async (list) => {
    let length = list.length;

    for(var i = 0; i < length; i++) {

        var min = i;
        for(var j = i + 1; j < length; j++) {
            // Executing statement comparison
            if(list[min].value > list[j].value) {
                // Updating our current min index to iterate
                min = j;
            }
            this.setState({
              array: list,
              active1: list[j].key,
              active2: list[min].key
            })
            await this.timer(15)
        }

        // Swaping values
        if(min !== i) {
            let temp = list[i];
            list[i] = list[min];
            list[min] = temp;
        }
    }
    this.setState({
      active1: 100,
      active2: 100
    })

    return list;
  }

  insertionsort = async (list) => {
    let auxArray = list.slice();
    let length = auxArray.length;

    for (let i = 1; i < length; i++) {
        let key = list[i];
        let j = i - 1;
        while (j >= 0 && auxArray[j].value > key.value) {
          this.setState({
            array: auxArray,
            active1: auxArray[j].key,
            active2: auxArray[i].key
          })
          await this.timer(15);
          auxArray[j + 1] = auxArray[j];
          auxArray[j] = {key: 100, value: auxArray[j].value};
          j--;
        }
        auxArray[j + 1] = key;
    }

    //get rid of active "red" bars
    this.setState({
      array: auxArray,
      active1: 101,
      active2: 101
    })
  }

  //------------------------------ to implement --------------------------//


  delayLoop = (fn, delay) => {
    return (name) => {
      setTimeout(() => {
        fn(name);
      }, delay * 1000);
    }
  }


  sleep(milliseconds) {
    const date = Date.now();
    let currentDate = null;
    do {
      currentDate = Date.now();
    } while (currentDate - date < milliseconds);
  }



  mergesort = async (list) => {
    var n = list.length;
    var m = 1;
    var right;
    var left;
    var sorted; 
    var maxIndex;
    //var auxArray; 

    while (m < n){
      var i = 0;
      while(i< n - m){
        //slice
        left = list.slice(i, i+m);
        maxIndex = Math.min(i+2*m, n);
        right = list.slice(i+m, maxIndex);
        sorted = [];
        //auxArray = [];

        //merge
        while(left.length && right.length){
          await this.timer(15);

          if(left[0].value <= right[0].value){
            sorted.push(left.shift());
          }else{
            sorted.push(right.shift());
          }

        }

        while(left.length){
          sorted.push(left.shift());
        }

        while(right.length){
          sorted.push(right.shift());
        }



        //recreate list, with sorted part
        var x = list.slice(0, i);
        var y = list.slice(maxIndex, list.length);
        list = x.concat(sorted, y);

        await this.timer(15);
        this.setState({
          array: list,
          active1: list[i].key,
          active2: list[maxIndex-1].key
        });

        i = i + 2*m
      }
      m = m * 2;

      this.setState({
        active1: 101,
        active2: 101
      })
    }
    return list
  }


  swap = (items, leftIndex, rightIndex) => {
    var temp = items[leftIndex];
    items[leftIndex] = items[rightIndex];
    items[rightIndex] = temp;
  }


  heapsort = async (list) => {
    //var res = this.heapify(list);

    for (var i = 1; i<list.length; i++){
      //if child is bigger than parent
      if(list[i].value > list[parseInt((i-1)/2)].value){
        var j = i;

        //while parent is smaller, swap child and parent
        while(list[j].value > list[parseInt((j-1)/2)].value){
          var temp = list[j];
          list[j] = list[parseInt((j-1)/2)];
          list[parseInt((j-1)/2)] = temp;

          this.setState({
            array: list,
            active1: i,
            active2: j
          });
          j = parseInt((j - 1) / 2);
          await this.timer(15)
        }
      }
    }

    var length = list.length;

    for(var i = length - 1; i>0; i--){
      //swap value of first index with last index
      var temp = list[0];
      list[0] = list[i];
      list[i] = temp;

      //maintaining heap property, after each swap 
      var j = 0;
      var index;

      while(true){
        await this.timer(15);

        index = 2*j+1;

        //if left child is smaller than right child, point index to right child
        if(index < (i - 1) && list[index].value < list[index + 1].value){
          index++;
        }

        //if parent is smaller than child, swap them with child having higher value
        if(index < i && list[j].value < list[index].value){
          temp = list[j];
          list[j] = list[index];
          list[index] = temp;
        }
        this.setState({
          array: list,
          active1: list[i].key,
          active2: list[j].key
        });

        j = index;
        if(index >= i){
          break;
        }
      }
    }
    this.setState({
      array: list,
      active1: 101,
      active2: 101
    });
  }


  quicksort = async (list, low, high) => {
    var size = high - low + 1;

    //create auxiliary stack
    var stack = new Array(size);

    //initialize top of the stack
    var top = -1;

    //push initial values of high and low to the stack
    stack[++top] = low;
    stack[++top] = high;

    while(top >= 0){
      high = stack[top--];
      low = stack[top--];

      //set pivot element in its correct position, in sorted array
      //var pivot = this.partition(list, low, high);

      // partition method
      var p = list[high];
      var i = low - 1;

      for(var j = low; j<=high-1; j++){
        if(list[j].value <= p.value){
          i++;
          await this.timer(20);
          this.swap(list, i, j);
          // var temp = list[i];
          // list[i] = list[j];
          // list[j] = temp;

          this.setState({
            array: list,
            active1: p.key,
            active2: j
          })          
        }
      } 

      this.swap(list, i+1, high);
      // temp = list[i+1];
      // list[i+1] = list[high];
      // list[high] = temp;

      var pivot = i + 1;

      //end of partition 

      await this.timer(20);
      this.setState({
        array: list,
        active1: high,
        active2: low
      })

      //if there are elements on left side of pivot, push left side to stack
      if (pivot - 1 > low){
        stack[++top] = low;
        stack[++top] = pivot - 1;
      }

      //if there are elements on right side of pivot, push right side to stack
      if (pivot+1 < high){
        stack[++top] = pivot + 1;
        stack[++top] = high;
      }
    }
    this.setState({
      array: list,
      active1: 101,
      active2: 101
    })
    return stack;
  }


  onStart = () => {
    let algorithm = this.state.algorithm;
    if(algorithm === 'bubblesort'){
      this.bubblesort(this.state.array)
    }else if (algorithm === 'selectionsort') {
      this.selectionsort(this.state.array);
    }else if(algorithm === 'insertionsort'){
      this.insertionsort(this.state.array)
    }else if(algorithm === 'mergesort'){
      this.mergesort(this.state.array);
    }else if(algorithm === 'heapsort'){
      this.heapsort(this.state.array);
    }else if(algorithm === 'quicksort'){
      this.quicksort(this.state.array, 0, this.state.array.length - 1);
    }
  };


  render(){
    return (
      <Router>
        <div className="App">
          <Header handleNewArray={this.handleNewArray} onStart={this.onStart}/>
          <Switch>
            <Route path='/doubleContainer'> 
              <DoubleContainer 
                array={this.state.array} 
                algorithm={this.state.algorithm}
                algorithm2={this.state.algorithm2}
                algorithmSelection={this.algorithmSelection}
              />
            </Route>
            <Route path='/multipleContainer'> 
              <MultipleContainer 
                array={this.state.array} 
                algorithm={this.state.algorithm}
                algorithm2={this.state.algorithm2}
                algorithm3={this.state.algorithm3}
                algorithm4={this.state.algorithm4}
                algorithmSelection={this.algorithmSelection}
              />
            </Route>
            <Route path='/'> 
              <SingleContainer array={this.state.array} 
                active1={this.state.active1}
                active2={this.state.active2}
                algorithm={this.state.algorithm}
                algorithmSelection={this.algorithmSelection}
              />
            </Route>
          </Switch>
        </div>
      </Router>
    );
  }

}

