import React from 'react';
import BarElem from './BarElem';

class SingleContainer extends React.Component{
	state = {
		array: []
	}

	changeArr = () => {}

	handleNewArray = () => {
		console.log("success");
		// const arr = new Array(10);
		// for (var i = 0; i<arr.length; i++){
		// 	var rand = Math.floor(Math.random() * 600);
		// 	arr.push(rand);
		// }
		// this.setState({
		// 	array: arr
		// })
	}

	render(){
		return(
			<div className="singleContainer" onClick={() => this.changeArr()}>

				{this.state.array.map(elem => (
					<BarElem value={elem} key={elem}/>
				))}
				<div style={{height: "8vh", backgroundColor: "#ff0000"}}>
				</div>
				<button onClick={()=>this.handleNewArray()}>Click me</button>
			</div>
		);
	}
}

export default SingleContainer;
