import React from 'react';
import BarElem from './BarElem';

class DoubleContainer extends React.Component{
	state = {
		algorithm1: "quicksort",
		algorithm2: "heapsort"
	}

	algorithm1Change = (event) => {
		this.setState({
			algorithm1: event.target.value
		})
	}

	algorithm2Change = (event) => {
		this.setState({
			algorithm2: event.target.value
		})
	}

	render(){
		return (
			<div className="doubleContainer cont">
				<div>
				<button>Select sort</button>
				{this.props.array.map(elem => (
					<BarElem value={elem.value * 0.35} key={elem.key} width={0.5}/>
				))}
				</div>
				<div>
				<button>Select sort</button>
				{this.props.array.map(elem => (
					<BarElem value={elem.value * 0.35} key={elem.key} width={0.5}/>
				))}
				</div>
			</div>
		);
	}

}

export default DoubleContainer;
